import React from 'react'

const App = () => {
  const cart =[
  {
    title: "Biscuit",
    amount: 6,
    price: 599,
  },

  {
    title: "Garri",
    amount: 1,
    price: 200,
  },

  {
    title: "Drinks",
    amount: 5,
    price: 1000,
  },
]

const total = cart.reduce((accumulator, cartNumber)=>{
  // defining values
  const {amount, price}= cartNumber;
  accumulator.totalItems +=amount;
  accumulator.cartTotal +=amount *price;
  return accumulator
}, {
  totalItems: 0,
  cartTotal: 0
})
const cartTotals = total.cartTotal.toFixed()

  return (
    <div>
      <p>Total items: {total.totalItems}</p>
      <p>Cart total: ${total.cartTotals}</p>
    </div>
  )
}

export default App
